import { Text, View, StyleSheet, Image } from 'react-native'
import React, { useState, useEffect } from 'react'
import axios from 'axios';

export default function ProfilePage({ navigation, route }) {

    const { access_token } = route.params;
    const [infos, setinfos] = useState([]);

    const getUserInfo = async () => {
        const url = 'https://masurao.fr/api/employees/me';
        const headers = {
            'accept': 'application/json',
            'X-Group-Authorization': 'AhSyHQjIpPhIvfI5OU8HHe9nyhPKwY-q',
            'Authorization': 'Bearer ' + access_token
        };

        try {
            console.log("tes3t");
            const response = await axios.get(url, { headers });
            console.log(response.data);
            setinfos(response.data);
        } catch (error) {
            console.log("test");
            console.log(error.status);
            return [];
        }
    };

    useEffect(() => {
        getUserInfo();
    }, []);

    return (
        <View>
            <View style={styles.container}>
                <Image
                    source={{
                        uri: `https://masurao.fr/api/employees/${infos.id}/image`,
                        method: 'GET',
                        headers: {
                            accept: 'application/json',
                            'X-Group-Authorization': 'AhSyHQjIpPhIvfI5OU8HHe9nyhPKwY-q',
                            Authorization: 'Bearer eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6NzQsImVtYWlsIjoib2xpdmVyLmxld2lzQG1hc3VyYW8uanAiLCJuYW1lIjoiT2xpdmVyIiwic3VybmFtZSI6Ikxld2lzIiwiZXhwIjoxNjk1NzI2MzMwfQ.kfZoJqTF7H6Wg1egKTyA8W3r-pucZvkmvBAP0v_Fb6k',
                        },
                    }}
                    style={styles.image} />
                <Text style={styles.name}> {infos.name}</Text>
            </View>
        </View>
    )
}

const styles = StyleSheet.create({
    image: {
        width: 200,
        height: 200,
        margin: 10,
        borderRadius: 50,
        borderWidth: 1,
        borderColor: '#000',
    },
})
